import "./App.css";
import { Component } from "react";

class App extends Component {
  state = {
    counter: 0,
    posts: [
      {
        id: 1,
        title: "O titulo 1",
        body: "Corpo do post 1",
      },
      {
        id: 2,
        title: "O titulo 2",
        body: "Corpo do post 2",
      },
      {
        id: 3,
        title: "O titulo 3",
        body: "Corpo do post 3",
      },
    ],
  };

  timeOutUpdate = null;

  componentDidMount() {
    this.handleTimeOut();
  }

  componentDidUpdate() {
    clearTimeout(this.timeOutUpdate);
    this.handleTimeOut();
  }

  handleTimeOut = () => {
    const { posts, counter } = this.state;
    posts[0].title = "O titulo mudou";

    this.timeOutUpdate = setTimeout(() => {
      this.setState({ posts, counter: counter + 1 });
    }, 1000);
  };

  render() {
    const { posts, counter } = this.state;
    return (
      <div className="App">
        <h1>{counter}</h1>
        {posts.map((post) => (
          <div key={post.id}>
            <h1>{post.title}</h1>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    );
  }
}

export default App;
